// src/components/ShakesCarousel.jsx
import React, { useRef } from "react";
import { FiChevronLeft, FiChevronRight, FiThumbsUp } from "react-icons/fi";

const SHAKES = [
  { id: 1, name: "Mocha Shake", price: 20, likes: 30, img: "data:image/jpeg;base64," },
  { id: 2, name: "Lavender Shake", price: 20, likes: 30, img: "data:image/jpeg;base64," },
  { id: 3, name: "Caramel Shake", price: 20, likes: 30, img: "data:image/jpeg;base64," },
  { id: 4, name: "Vanilla Bean", price: 18, likes: 24, img: "data:image/jpeg;base64," },
  { id: 5, name: "Strawberry Bliss", price: 19, likes: 42, img: "data:image/jpeg;base64," },
  { id: 6, name: "Chocolate Dream", price: 21, likes: 56, img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQXnM_3JQ2d1BV-_co-iiMZK0_EVFoeWcqzew&s" },
  { id: 7, name: "Mango Tango", price: 17, likes: 18, img: "data:image/jpeg;base64," },
  { id: 8, name: "Pistachio Shake", price: 22, likes: 12, img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSMd83ld02oQxwFfsN99HxE5wbZ8f7HfrXwkA&s" },
  { id: 9, name: "Oreo Fantasy", price: 20, likes: 34, img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTluhbM6Xbd984yg8Ot-kYM8yXz8Wtrsh5rOA&s" },
  { id: 10, name: "Berry Burst", price: 18, likes: 28, img: "data:image/jpeg;base64," },
];

export default function ShakesCarousel() {
  const scrollerRef = useRef(null);

  const scrollBy = (dir = "right") => {
    const el = scrollerRef.current;
    if (!el) return;

    const cardWidth = el.querySelector("[data-card]")?.offsetWidth || 300;
    const gap = 24;
    const offset = cardWidth + gap;
    const distance = dir === "right" ? offset * 2 : -offset * 2;

    el.scrollBy({ left: distance, behavior: "smooth" });
  };

  return (
    <section className="py-14 px-6 md:px-12 bg-black text-white">
      <div className="max-w-7xl mx-auto">

        {/* Title + Buttons */}
        <div className="flex items-center justify-between mb-8">
          <div>
            <h2 className="text-3xl font-bold tracking-wide">Top Milk Shakes</h2>
            <p className="text-gray-400 text-sm">
              Explore the recent most bought shakes this week
            </p>
          </div>

          <div className="flex gap-3">
            <button
              onClick={() => scrollBy("left")}
              aria-label="Scroll Left"
              className="p-3 rounded-full bg-[#111] hover:bg-orange-500 hover:scale-110 transition-all shadow-lg"
            >
              <FiChevronLeft className="w-5 h-5" />
            </button>

            <button
              onClick={() => scrollBy("right")}
              aria-label="Scroll Right"
              className="p-3 rounded-full bg-[#111] hover:bg-orange-500 hover:scale-110 transition-all shadow-lg"
            >
              <FiChevronRight className="w-5 h-5" />
            </button>
          </div>
        </div>

        {/* Carousel */}
        <div className="relative">
          <div
            ref={scrollerRef}
            className="flex gap-6 overflow-x-auto pb-4 scroll-smooth snap-x snap-mandatory no-scrollbar"
          >
            {SHAKES.map((s) => (
              <div
                key={s.id}
                data-card
                className="snap-start flex-none w-[300px] bg-[#111] rounded-2xl p-6 
                shadow-lg hover:shadow-orange-500/20
                transform transition-all duration-300 hover:-translate-y-2 hover:scale-[1.03]"
              >
                {/* Likes Badge */}
                <div className="flex justify-end">
                  <div className="bg-black/80 border border-gray-800 px-3 py-1 rounded-full flex items-center gap-2 text-xs">
                    <FiThumbsUp className="text-orange-400" />
                    {s.likes} likes
                  </div>
                </div>

                {/* Image */}
                <div className="mt-4 mb-4 flex justify-center">
                  <div className="w-40 h-40 rounded-xl overflow-hidden bg-[#0a0a0a]">
                    <img src={s.img} className="w-full h-full object-cover" alt={s.name} />
                  </div>
                </div>

                {/* Item Info */}
                <h3 className="text-lg font-semibold text-center">{s.name}</h3>

                <div className="flex items-center justify-between mt-4">
                  <div>
                    <p className="text-gray-500 text-xs">Price</p>
                    <p className="text-xl font-bold">${s.price}</p>
                  </div>

                  <button
                    className="px-4 py-2 text-xs rounded-full bg-orange-500 hover:bg-orange-600
                    transition-all shadow-md hover:scale-110"
                  >
                    BUY NOW
                  </button>
                </div>
              </div>
            ))}
          </div>

          {/* Gradient Edges */}
          <div className="pointer-events-none absolute left-0 top-0 bottom-0 w-10 bg-gradient-to-r from-black to-transparent"></div>
          <div className="pointer-events-none absolute right-0 top-0 bottom-0 w-10 bg-gradient-to-l from-black to-transparent"></div>
        </div>
      </div>

      {/* Hide Scrollbar CSS */}
      <style>{`
        .no-scrollbar::-webkit-scrollbar { display: none; }
        .no-scrollbar { scrollbar-width: none; }
      `}</style>
    </section>
  );
}
